mapper.insert:
insert into task (id, course_id, title, 
  requires, `desc`, grade_sn
  )
values (#{id,jdbcType=INTEGER}, #{courseId,jdbcType=INTEGER}, #{title,jdbcType=VARCHAR}, 
  #{requires,jdbcType=VARCHAR}, #{desc,jdbcType=VARCHAR}, #{gradeSn,jdbcType=VARCHAR}
  )

mapper.select:
select *
from task
where course_id = #{courseId}
and grade_sn = #{gradeSn}
